import { Component } from '@angular/core';
import { VgApiService } from '@videogular/ngx-videogular/core';

@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.css']
})
export class PlayerComponent {
  api: VgApiService | undefined;
  constructor(){

  }

  onPlayerSet(api: VgApiService){
    this.api = api
  }
}
<div class="contain-fuild">
  <vg-player (onPlayerReady)="onPlayerSet($event)">
    <video [vgMedia]="$any(media)" #media preload="auto" controls autoplay>
      <source src="https://storage.googleapis.com/exoplayer-test-media-1/gen-3/screens/dash-vod-single-segment/video-avc-baseline-480.mp4"/>
    </video>
  </vg-player>
</div>
