import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { MovieApiService } from 'src/app/services/movie-api.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {

  constructor(private service:MovieApiService) {}

  searchResult:any;

  searchForm = new FormGroup({
    'movieName': new FormControl(null)
  });

  submitForm(){
    this.service.getSearchMovie(this.searchForm.value).subscribe((result) => {
      console.log(result, "searchResult#");
      this.searchResult = result.results;
    });
  }
}
<div class="contain mt-5 p-5">
  <form [formGroup]="searchForm" (ngSubmit)="submitForm()">
    <div class="row">
      <div class="mb-3 col-lg-10">
        <input type="text" class="form-control" formControlName="movieName" placeholder="Search movie here..." />
      </div>
      <div class="col-lg-2">
        <button class="btn btn-md">
          Search
        </button>
      </div>
    </div>
  </form>
</div>

<div class="row mt-5">
  <div class="col-lg-4" *ngFor="let s of searchResult">
    <div class="row">
      <div class="col-lg-3">
        <img src="https://image.tmdb.org/t/p/original/{{ s.poster_path }}" />
      </div>
      <div class="col-lg-9">
          <h5>{{s.title}}</h5>
      </div>
    </div>
  </div>
</div>
